import CardDefinitions from "../Components/CardDefinitions";

function fight(card1, card2) {
  let card1Health = card1.health;
  let card2Health = card2.health;
  let card1Keywords = card1.keywords;
  let result = null;
  if (card1.attack === 0 && card2.attack === 0) {
    return "draw";
  }
  while (card1Health > 0 && card2Health > 0) {
    card1Health = card1Health - card2.attack;
    card2Health = card2Health - card1.attack;
  }
  if (card1Health > 0) {
    result = "win";
  } else if (card2Health > 0) {
    result = "loose";
  } else {
    result = "draw";
  }
  return result;
}

export function fightAll(card) {
  let results = CardDefinitions.map((opponent) => {
    return fight(card, opponent);
  });
  return {
    win: results.filter((result) => result === "win").length,
    loose: results.filter((result) => result === "loose").length,
    draw: results.filter((result) => result === "draw").length,
  };
}
