export default function SelectedCards({ selectedCards, handleCardRemove }) {
  return selectedCards.map((selectedCard, i) => {
    if (selectedCard) {
      return (
        <div
          key={selectedCard.name + i}
          onClick={() => {
            handleCardRemove();
          }}
        >
          {selectedCard.name}
        </div>
      );
    }
    return <div key={i}>Empty</div>;
  });
}
