export default function SelectedCards({ selectedCard }) {
  if (selectedCard) {
    return (
      <div
        onClick={() => {
          if (selectedCard) {
            setSelectedCard();
          }
        }}
      >
        {selectedCard.name}
      </div>
    );
  }
  return <p>No card selected.</p>;
}
