export default function SelectedCards({ selectedCards, handleCardRemove }) {
  selectedCards.map((selectedCard, i) => {
    if (selectedCard) {
      return (
        <div
          className="Card-Box"
          key={selectedCard.name + i}
          onClick={() => {
            handleCardRemove(i);
          }}
        >
          <img className="Card-Image" src={selectedCard.picture}></img>
          <p>{selectedCard.name}</p>
        </div>
      );
    }
    return (
      <div className="Card-Box" key={i}>
        Empty
      </div>
    );
  });
}
