import { useState } from "react";
import "./styles.css";
import cards from "./CardDefinitions.js";

export default function AllCards() {
  const [minions, setMinions] = useState(cards);
  const [selectedCard, setSelectedCard] = useState(null);
  return (
    <div className="CardGrid">
      {minions.map(
        ({ name, attack, health, species, keywords, minion, picture }) => {
          <div
            onClick={(event) => {
              setSelectedCard();
            }}
            className="Card"
            key={name}
          >
            <h2>{name}</h2>
            <img className="Card-Image" src={picture}></img>
            <p>
              <span>attack: {attack} </span>
              <span>health: {health}</span>
            </p>
            <p>species: {asList(species)}</p>
            <p>keywords: {asList(keywords)}</p>
            <p>has minion?: {minion.length > 0 ? "true" : "false"}</p>
          </div>;
        }
      )}
    </div>
  );
}

function asList(array) {
  return array.join(", ");
}
