import CardDefinitions from "../Components/CardDefinitions";

export function fight(card1, card2) {
  let card1Health = card1.health;
  let card2Health = card2.health;
  let result = null;
  if (card1.attack === 0 && card2.attack === 0) {
    return "draw";
  }
  while (card1Health > 0 && card2Health > 0) {
    card1Health = card1Health - card2.attack;
    card2Health = card2Health - card1.attack;
  }
  console.log(card1Health, card2Health);
  if (card1Health > 0) {
    result = "win";
  } else if (card2Health > 0) {
    result = "loose";
  } else {
    result = "draw";
  }
  console.log(result);
}
fight(CardDefinitions[0], CardDefinitions[5]);
