import { useState } from "react";
import "./styles.css";
import cards from "./CardDefinitions.js";

export default function AllCards({ setSelectedCard, selectedCard }) {
  const [minions, setMinions] = useState(cards);
  return (
    <div className="CardGrid">
      {minions.map((card) => (
        <div
          onClick={() => {
            if (!selectedCard) {
              setSelectedCard(card);
            }
          }}
          className="Card"
          key={card.name}
        >
          <h2>{card.name}</h2>
          <img className="Card-Image" src={card.picture}></img>
          <p>
            <span>attack: {card.attack} </span>
            <span>health: {card.health}</span>
          </p>
          <p>species: {asList(card.species)}</p>
          <p>keywords: {asList(card.keywords)}</p>
          <p>has minion?: {card.minion.length > 0 ? "true" : "false"}</p>
        </div>
      ))}
    </div>
  );
}

function asList(array) {
  return array.join(", ");
}
