import CardDefinitions from "../Components/CardDefinitions";

function fight(card1, card2) {
  let myCard = { ...card1 };
  let opponent = { ...card2 };
  let result = null;

  // Prevent endless loop when both have 0 attack
  if (myCard.attack === 0 && opponent.attack === 0) {
    return "draw";
  }

  // Check for Divine Shield and simulate the first attack without getting damaged
  if (
    myCard.keywords.includes("Divine Shield") ||
    opponent.keywords.includes("Divine Shield")
  ) {
    if (myCard.keywords.includes("Divine Shield")) {
      opponent.health = opponent.health - myCard.attack;
    } else {
      myCard.health = myCard.health - opponent.attack;
    }
  }

  while (myCard.health > 0 && opponent.health > 0) {
    myCard.health = myCard.health - opponent.attack;
    opponent.health = opponent.health - myCard.attack;
  }
  if (myCard.health > 0) {
    result = "win";
  } else if (opponent.health > 0) {
    result = "loose";
  } else {
    result = "draw";
  }
  return result;
}

export function fightAll(myCard) {
  let results = CardDefinitions.map((opponent) => {
    return fight(myCard, opponent);
  });
  return {
    win: results.filter((result) => result === "win").length,
    loose: results.filter((result) => result === "loose").length,
    draw: results.filter((result) => result === "draw").length,
  };
}
