import React from "react";
import CardGrid from "./CardGrid";
import "./styles.css";
import SelectedCards from "./SelectedCards";
import { useState } from "react";

export default function App() {
  const [selectedCards, setSelectedCards] = useState([null, null, null]);

  function handleCardSelection(card) {
    const emptyIndex = selectedCards.findIndex(
      (selectedCard) => selectedCard === null
    );
    console.log(emptyIndex);
    const newSelectedCards = [...selectedCards];
    if (emptyIndex !== -1) {
      newSelectedCards[emptyIndex] = card;
      console.log(newSelectedCards);
      setSelectedCards(newSelectedCards);
    }
  }

  function handleCardRemove() {
    setSelectedCards(null);
  }

  return (
    <main>
      <CardGrid handleCardSelection={handleCardSelection} />
      <SelectedCards
        selectedCards={selectedCards}
        handleCardRemove={handleCardRemove}
      />
    </main>
  );
}
